clcclear%load annotationload('annotation_2.mat');%Set data rootDataRoot='./DataSet_002/';fid = fopen(['./','Annotations-export2.csv'], 'w' );fprintf( fid, '%s,%s,%s,%s,%s,%s\n', 'image','xmin','ymin','xmax','ymax','label');for i=1:numel(annot)    %Read image    im=imread([DataRoot,annot(i).ImgName]);    imName = [DataRoot,annot(i).ImgName];        for j=1:size(annot(i).BBox,1)        x1=annot(i).BBox(j,1);        y1=annot(i).BBox(j,2);        x2=annot(i).BBox(j,3);        y2=annot(i).BBox(j,4);                label = 'player';        fprintf( fid, '%s,%d,%d,%d,%d,%s\n', imName, x1, y1, x2, y2, label);    endenddisp('Done!');